            color: var(--text-main);
            margin-right: 4px;
        }

        /* Utils */
        .empty { font-style: italic; color: #999; font-size: 12px; padding: 4px 0; text-align: center;}
        .error-tip { color: #b83232; font-size: 12px; padding: 8px; background: #ffe9e9; border-radius: 4px; }
    </style>
</head>
<body>

<div class="compact-container" id="app">
    <!-- Header -->
    <header class="header-bar">
        <div class="header-title">身体交换日常</div>
        <div class="header-meta">
            <div class="meta-item" title="时间">
                <span>🕒</span><span class="meta-val" id="time">--</span>
            </div>
            <div class="meta-item" title="地点">
                <span>📍</span><span class="meta-val" id="location">--</span>
            </div>
        </div>
    </header>

    <!-- Navigation -->
    <nav class="nav-tabs">
        <button class="tab-btn active" onclick="setTab('chars', this)">当前角色</button>
        <button class="tab-btn" onclick="setTab('sakura', this)">白鸟樱</button>
    </nav>

    <!-- Content -->
    <div class="content-area">
        <!-- Characters Tab -->
        <div id="tab-chars" class="tab-pane active">
            <div class="section">
                <div id="char-container"></div>
            </div>
        </div>

        <!-- Sakura Tab -->
        <div id="tab-sakura" class="tab-pane">
            <div class="section">
                <div class="stat-row">
                    <div class="stat-name">好感度</div>
                    <div class="progress-track">
                        <div id="sakura-bar" class="progress-fill" style="width: 0%"></div>
                    </div>
                    <div class="stat-val" id="sakura-val">0</div>
                </div>
            </div>
            <div class="section">
                <div class="section-title">重要记忆</div>
                <div id="memory-container" class="mem-list"></div>
            </div>
        </div>
    </div>
</div>

<script>
    function setTab(tabId, btn) {
        // Update buttons
        document.querySelectorAll('.tab-btn').forEach(b => b.classList.remove('active'));
        if(btn) btn.classList.add('active');

        // Update panes
        document.querySelectorAll('.tab-pane').forEach(p => p.classList.remove('active'));
        const pane = document.getElementById('tab-' + tabId);
        if(pane) pane.classList.add('active');
    }

    function SafeGetValue(obj, path, defaultValue = "N/A") {
        let keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !current.hasOwnProperty(keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        return (current === undefined || current === null) ? defaultValue : current;
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            if (!messages || messages.length === 0 || !messages[0].data) {
                document.getElementById('app').innerHTML = "<div class='error-tip'>无法加载数据 (messages empty)</div>";
                return;
            }
            const gameData = messages[0].data;
            const characterData = gameData.stat_data;
            if (!characterData) {
                document.getElementById('app').innerHTML = "<div class='error-tip'>无法加载数据 (stat_data missing)</div>";
                return;
            }

            // Basic Info
            document.getElementById('time').innerText = SafeGetValue(characterData, '世界.当前时间');
            document.getElementById('location').innerText = SafeGetValue(characterData, '世界.当前地点');

            // Characters
            const charContainer = document.getElementById('char-container');
            const currentCharacters = characterData['当前角色'];
            if (currentCharacters && typeof currentCharacters === 'object') {
                let html = '';
                for (const charName in currentCharacters) {
                    const char = currentCharacters[charName];
                    html += `
                        <div class="char-row">
                            <div class="char-header">
                                <div class="char-name">${charName}</div>
                            </div>
                            <div class="char-detail">
                                <div class="label">想法</div>
                                <div class="text">${char['当前想法'] || '-'}</div>
                                <div class="label">状态</div>
                                <div class="text">${char['当前状态'] || '-'}</div>
                                <div class="label">穿着</div>
                                <div class="text">${char['当前穿着'] || '-'}</div>
                            </div>
                        </div>
                    `;
                }
                charContainer.innerHTML = html || '<div class="empty">暂无角色</div>';
            } else {
                charContainer.innerHTML = '<div class="empty">暂无角色</div>';
            }

            // Sakura Stats
            const affection = parseFloat(SafeGetValue(characterData, '白鸟樱.好感度', 0));
            document.getElementById('sakura-val').innerText = affection;
            document.getElementById('sakura-bar').style.width = Math.max(0, Math.min(100, affection)) + '%';

            // Memories
            const memContainer = document.getElementById('memory-container');
            const memories = characterData['白鸟樱']?.['重要记忆'];
            if (memories && typeof memories === 'object') {
                let html = '';
                for (const key in memories) {
                    html += `
                        <li class="mem-item">
                            <span class="mem-key">${key}:</span>
                            ${memories[key]}
                        </li>
                    `;
                }
                memContainer.innerHTML = html || '<div class="empty">暂无记忆</div>';
            } else {
                memContainer.innerHTML = '<div class="empty">暂无记忆</div>';
            }

        } catch (e) {
            document.getElementById('app').innerHTML = `<div class='error-tip'>加载错误: ${e.message}</div>`;
        }
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
</body>
</html>
