            const worldLocationElem = document.getElementById('world-location');
            if (worldLocationElem) worldLocationElem.innerText = SafeGetValue(characterData, '世界.当前地点');

            const yuexhuOutfitElem = document.getElementById('yuexhu-outfit');
            if (yuexhuOutfitElem) yuexhuOutfitElem.innerText = SafeGetValue(characterData, '沈月舒.当前着装');

            const yuexhuPoseElem = document.getElementById('yuexhu-pose');
            if (yuexhuPoseElem) yuexhuPoseElem.innerText = SafeGetValue(characterData, '沈月舒.当前姿势');

            const yuexhuThoughtsElem = document.getElementById('yuexhu-thoughts');
            if (yuexhuThoughtsElem) yuexhuThoughtsElem.innerText = SafeGetValue(characterData, '沈月舒.当前想法');

            const xingyaoOutfitElem = document.getElementById('xingyao-outfit');
            if (xingyaoOutfitElem) xingyaoOutfitElem.innerText = SafeGetValue(characterData, '沈星遥.当前着装');

