<script>
    function SafeGetValue(obj, path, defaultValue = "N/A") {
        let keys = Array.isArray(path) ? path : path.split('.');
        let current = obj;
        for (let i = 0; i < keys.length; i++) {
            if (current === undefined || current === null || typeof current !== 'object' || !current.hasOwnProperty(keys[i])) {
                return defaultValue;
            }
            current = current[keys[i]];
        }
        if (current === undefined || current === null) {
            return defaultValue;
        }
        if (Array.isArray(current)) {
            if (current.length > 0) {
                const actualValue = current[0];
                if (typeof actualValue === 'boolean') {
                    return actualValue;
                }
                return String(actualValue);
            } else {
                return defaultValue;
            }
        }
        if (typeof current === 'boolean') {
            return current;
        }
        if (typeof current === 'number') {
            return current;
        }
        if (typeof current === 'string') {
            return current;
        }
        return JSON.stringify(current);
    }

    function updateProgressBar(barId, valueMainId, valueDescId, rawValue, min = 0, max = 100, color) {
        const progressBar = document.getElementById(barId);
        const valueMainDisplay = document.getElementById(valueMainId);
        const valueDescDisplay = document.getElementById(valueDescId);
        if (!progressBar || !valueMainDisplay) {
            return;
        }
        let numericValue = parseFloat(rawValue);
        if (isNaN(numericValue)) {
            numericValue = parseFloat(String(rawValue).replace(/[^0-9.-]/g, ''));
        }
        if (!isNaN(numericValue)) {
            valueMainDisplay.innerText = numericValue;
        } else {
            valueMainDisplay.innerText = rawValue;
        }
        if (valueDescDisplay) {
            let desc = '';
            if (!isNaN(numericValue)) {
                if (numericValue < 10) desc = '陌生';
                else if (numericValue < 30) desc = '初识';
                else if (numericValue < 50) desc = '渐熟';
                else if (numericValue < 70) desc = '信赖';
                else if (numericValue < 90) desc = '深情';
                else desc = '相守终生';
            }
            valueDescDisplay.innerText = desc;
        }
        if (!isNaN(numericValue)) {
            numericValue = Math.max(min, Math.min(max, numericValue));
            const range = max - min;
            const percentage = range === 0 ? (numericValue >= max ? 100 : 0) : ((numericValue - min) / range) * 100;
            progressBar.style.width = `${Math.max(0, Math.min(100, percentage))}%`;
            progressBar.style.backgroundColor = color ? color : '#d4a574';
        }
    }

    async function initDisplay() {
        try {
            const messages = await getChatMessages(getCurrentMessageId());
            if (!messages || messages.length === 0 || !messages[0].data) {
                document.getElementById('status-card').innerHTML = "<div class='error-tip'>无法加载状态数据 (messages empty)</div>";
                return;
            }
            const gameData = messages[0].data;
            const characterData = gameData.stat_data;
            if (!characterData) {
                document.getElementById('status-card').innerHTML = "<div class='error-tip'>无法加载状态数据 (characterData missing)</div>";
                return;
            }

            const timeElem = document.getElementById('current-time');
            if (timeElem) timeElem.innerText = SafeGetValue(characterData, '世界.当前时间');

            const locationElem = document.getElementById('current-location');
            if (locationElem) locationElem.innerText = SafeGetValue(characterData, '世界.当前地点');

            updateProgressBar(
                'waner-affection-bar',
                'waner-affection',
                'waner-affection-desc',
                SafeGetValue(characterData, '柳婉儿.好感度', 0),
                0, 100
            );

            const wanerOutfit = document.getElementById('waner-outfit');
            if (wanerOutfit) wanerOutfit.innerText = SafeGetValue(characterData, '柳婉儿.当前着装');

            const wanerPose = document.getElementById('waner-pose');
            if (wanerPose) wanerPose.innerText = SafeGetValue(characterData, '柳婉儿.当前姿势');

            const wanerThoughts = document.getElementById('waner-thoughts');
            if (wanerThoughts) wanerThoughts.innerText = SafeGetValue(characterData, '柳婉儿.当前想法');

            const itemsListElem = document.getElementById('items-list');
            if (itemsListElem) {
                const itemsList = characterData['玩家'] ? characterData['玩家']['物品列表'] : null;
                if (Array.isArray(itemsList) && itemsList.length > 0) {
                    let itemsHtml = '';
                    itemsList.forEach((item, index) => {
                        itemsHtml += `<div class="list-item">${index + 1}. ${item}</div>`;
                    });
                    itemsListElem.innerHTML = itemsHtml;
                } else {
                    itemsListElem.innerHTML = '<div class="empty-tip">身无长物</div>';
                }
            }

            const assetsListElem = document.getElementById('assets-list');
            if (assetsListElem) {
                const assetsList = characterData['玩家'] ? characterData['玩家']['资产列表'] : null;
                if (assetsList && typeof assetsList === 'object') {
                    let assetsHtml = '';
                    let index = 1;
                    for (const assetKey in assetsList) {
                        if (!Object.prototype.hasOwnProperty.call(assetsList, assetKey)) {
                            continue;
                        }
                        const assetDesc = assetsList[assetKey];
                        assetsHtml += `<div class="list-item"><strong>${assetKey}</strong>：${assetDesc}</div>`;
                        index++;
                    }
                    assetsListElem.innerHTML = assetsHtml || '<div class="empty-tip">暂无资产</div>';
                } else {
                    assetsListElem.innerHTML = '<div class="empty-tip">暂无资产</div>';
                }
            }
        } catch (error) {
            document.getElementById('status-card').innerHTML = `<div class='error-tip'>状态栏加载出错: ${error.message}</div>`;
        }
    }

    document.addEventListener('DOMContentLoaded', initDisplay);
</script>
</body>
</html>

