        function updateBar(id, value, max = 100, color = '') {
            const val = _.clamp(parseFloat(value) || 0, 0, max);
            const pct = (val / max) * 100;
            $(`#${id}-val`).text(val);
            $(`#${id}-bar`).css('width', `${pct}%`);
            if (color) $(`#${id}-bar`).css('background-color', color);
        }

