                /* 基础状态 */
                updateBar('favor', _.get(data, '沈馨.好感度', 0), 100, '#ff6b6b');
                updateBar('shame', _.get(data, '沈馨.羞耻度', 80), 100, '#ff922b');
                updateBar('corruption', _.get(data, '沈馨.堕落度', 0), 100, '#ae3ec9');
                updateBar('anxiety', _.get(data, '沈馨.焦虑值', 90), 100, '#20c997');

                /* 外貌详情 */
                const appearance = _.get(data, '沈馨.外貌', {});

                // 胸部
                $('#chest-cup').text(_.get(appearance, '胸部.罩杯', 'G罩杯'));
                $('#chest-desc').text(_.get(appearance, '胸部.描述', ''));
                $('#chest-feature').text(_.get(appearance, '胸部.特征', ''));

                // 臀部
                $('#hip-size').text(_.get(appearance, '臀部.尺寸', '标准偏丰满'));
                $('#hip-desc').text(_.get(appearance, '臀部.描述', ''));
                $('#hip-feature').text(_.get(appearance, '臀部.特征', ''));

                // 腰部
                $('#waist-size').text(_.get(appearance, '腰部.尺寸', '纤细'));
                $('#waist-desc').text(_.get(appearance, '腰部.描述', ''));
                $('#waist-feature').text(_.get(appearance, '腰部.特征', ''));

                // 腿部
                $('#leg-desc').text(_.get(appearance, '腿部.描述', ''));
                $('#leg-feature').text(_.get(appearance, '腿部.特征', ''));

                // 面容
                $('#face-shape').text(_.get(appearance, '面容.脸型', '圆脸带婴儿肥'));
                $('#face-eyes').text(_.get(appearance, '面容.眼睛', ''));
                $('#face-nose').text(_.get(appearance, '面容.鼻子', ''));
                $('#face-lips').text(_.get(appearance, '面容.嘴唇', ''));
                $('#face-desc').text(_.get(appearance, '面容.描述', ''));

                // 皮肤
                $('#skin-color').text(_.get(appearance, '皮肤.肤色', '白皙'));
                $('#skin-texture').text(_.get(appearance, '皮肤.质感', ''));
                $('#skin-feature').text(_.get(appearance, '皮肤.特征', ''));

                // 头发
                $('#hair-length').text(_.get(appearance, '头发.长度', ''));
                $('#hair-style').text(_.get(appearance, '头发.发型', ''));
                $('#hair-feature').text(_.get(appearance, '头发.特征', ''));

                // 身高体重
                $('#height').text(_.get(appearance, '身高体重.身高', '158cm'));
                $('#weight').text(_.get(appearance, '身高体重.体重', '约52kg'));
                $('#body-type').text(_.get(appearance, '身高体重.体型', ''));

                /* 改造系统 */
                const records = _.get(data, '沈馨.改造记录', []);
                const $recordsList = $('#modifications-list');
                $recordsList.empty();

                if (_.isEmpty(records)) {
                    $recordsList.html('<div class="empty-tip">暂无改造记录</div>');
                } else {
                    _.forEach(records, (record) => {
                        const remarkHtml = record.备注 ? `<div class="record-remark">备注: ${record.备注}</div>` : '';
                        $recordsList.append(`
                            <div class="modification-record">
                                <div class="record-header">
                                    <span class="record-title">${record.部位} - ${record.改造内容}</span>
                                    <span class="record-date">${record.日期}</span>
                                </div>
                                <div class="record-content">
                                    <div>改造前: ${record.改造前状态}</div>
                                    <div>改造后: ${record.改造后状态}</div>
