                    updateBar('current-progress', _.get(currentMod, '进度', 0), 100, '#0ca678');
                } else {
                    $('.current-modification').hide();
                }

            } catch (e) {
                console.error("Status Bar Error:", e);
                $('#status-card').html(`<div style="color:red">Error: ${e.message}</div>`);
            }
        }

        async function init() {
